import React, { useState } from "react";
import Button from "./Button";
import Modal from "./Modal";


function Item({data}){
    const [openModal, setOpenModal] = useState(false);

    const handleDelete =()=>{
        console.log("deleting")
        setOpenModal(true);
    }

    const handleEdit =()=>{
        console.log("Editing")
    }


    return(
        <tr className="row">
            <td data-title="ItemID">{data.id}</td>
            <td data-title="UserID">{data.userId}</td>
            <td data-title="Title"> {data.title}</td>
            <td data-title="Body">{data.body}</td>
            <td data-title="Action" className="actions">
                <Button 
                    size={"md"} 
                    className={"delete"}
                    onClick={()=>handleDelete()}>Delete</Button>

                {openModal && 
                    <Modal
                        titleModal={'Are you sure you want "Delete Post"?'}
                        changeState={setOpenModal}

                        > <div style={{display: "flex", justifyContent: "space-between"}}>
                        <Button>Yes</Button>
                        <Button>No</Button></div>
                </Modal>}
                <Button 
                    size={"md"} 
                    className={"edit"}
                    onClick={()=>handleEdit()}>Edit</Button></td>
        </tr>



    );
}

export default Item;
