import React from "react";
import "../styles/button.css"

// const ButtonComponent = styled.button


function Button ({type, variant, className, id, onClick, children, size}){

    return(
        <button
        type={ type ? type : "button"}
        variant = {variant}
        className={className ? `btn-component ${className}` : "btn-component"}
        id={id}
        onClick={onClick}
        style={{padding: size === "sm" ? "5px" : size === "md" ? "0.5rem" : size === "lg" ? "1rem" : "5px"}}
            >{children}</button>
    );
}

export default Button;
